echo "Using:" >&2
echo "  STAGE0_BIN: ${STAGE0_BIN}" >&2
echo "  INITRD:     ${INITRD}" >&2
echo "  KERNEL:     ${KERNEL}" >&2
echo "  ROOTFS:     ${ROOTFS}" >&2
echo "" >&2

# Extra debugging knobs:
#   DEBUG=1   -> enable verbose QEMU logging to qemu.log
#   GDB=1     -> wait for gdb on tcp:1234 (-S -s)
QEMU_DEBUG_ARGS=()
if [[ "${DEBUG:-0}" == "1" ]]; then
  QEMU_DEBUG_ARGS+=(-d "guest_errors,unimp,cpu_reset,int" -D qemu.log)
fi
if [[ "${GDB:-0}" == "1" ]]; then
  QEMU_DEBUG_ARGS+=(-S -s)
fi

qemu-system-x86_64 \
  -enable-kvm \
  -cpu host \
  -m 1G \
  -machine pc \
  -no-reboot \
  -display none \
  -chardev stdio,id=ch0,mux=on,signal=off \
  -serial chardev:ch0 \
  -mon chardev=ch0,mode=readline \
  "${QEMU_DEBUG_ARGS[@]}" \
  -bios "${STAGE0_BIN}"

